mapper.select:
SELECT count(*)
FROM new_sg_in_relation a0 LEFT JOIN new_ip_network a1 ON a0.ipNetworkId = a1.id
WHERE a0.serverGroupId = #{serverGroupId}
AND a1.id IS NOT NULL;

SELECT a1.*
FROM new_sg_in_relation a0 LEFT JOIN new_ip_network a1 ON a0.ipNetworkId = a1.id
WHERE a0.serverGroupId = #{serverGroupId}
AND a1.id IS NOT NULL
limit #{pageStart}, #{pageLength};

mapper.insert:
INSERT INTO new_sg_in_relation(
  serverGroupId,
  ipNetworkId,
  ipType
) VALUES

(
    #{item.serverGroupId},
    #{item.ipNetworkId},
    #{item.ipType}
)
