mapper.update:
UPDATE keybox_application_key
SET
    publicKey = #{publicKey},
    privateKey = #{privateKey}
WHERE
    id = #{id}

mapper.insert:
INSERT ignore INTO keybox_user_server(
    username,
    serverGroupId
) VALUES (
    #{username},
    #{serverGroupId}
);

mapper.update:
UPDATE keybox_user_server
SET
    username = #{username},
    serverGroupId = #{serverGroupId}
WHERE
    id = #{id}

mapper.delete:
DELETE FROM keybox_user_server

    username = #{username}

        AND serverGroupId = #{serverGroupId}

mapper.select:
SELECT count(*) FROM keybox_user_server


        serverGroupId = #{serverGroupId}


        AND username = #{username}

SELECT * FROM keybox_user_server


        serverGroupId = #{userServer.serverGroupId}


        AND username = #{userServer.username}


order BY serverGroupId DESC
limit #{pageStart}, #{pageLength}

SELECT a0.* FROM oc_server_group a0 JOIN (
SELECT serverGroupId FROM keybox_user_server
WHERE username = #{username}
) a1 on a0.id = a1.serverGroupId

SELECT count(DISTINCT a2.id)
FROM keybox_user_server a0
JOIN oc_server_group a1 ON a0.serverGroupId = a1.id
JOIN oc_server a2 ON a1.id = a2.serverGroupId
WHERE username = #{username}

    AND a0.serverGroupId = #{serverGroupId}


    AND envType = #{envType}

SELECT a2.*
FROM keybox_user_server a0
JOIN oc_server_group a1 ON a0.serverGroupId = a1.id
JOIN oc_server a2 ON a1.id = a2.serverGroupId
WHERE username = #{username}

    AND a0.serverGroupId = #{serverGroupId}


    AND envType = #{envType}

ORDER BY a2.serverGroupId
limit #{pageStart}, #{pageLength}

SELECT * FROM keybox_user_server
WHERE serverGroupId = #{serverGroupId}
order BY username DESC
limit #{pageStart}, #{pageLength}

mapper.insert:
INSERT INTO keybox_login_status(
userId,
username,
serverId,
loginType
) VALUES (
#{userId},
#{username},
#{serverId},
#{loginType}
);

mapper.select:
SELECT * FROM keybox_login_status
WHERE loginType = #{loginType}
ORDER BY gmtCreate DESC
limit 10;
