mapper.select:
SELECT * FROM log_service_cfg
serverName LIKE "%" #{serverName} "%"
ORDER BY serverName DESC limit #{start}, #{length}

mapper.insert:
INSERT INTO log_histograms (
logServiceId,
timeFrom,
timeTo,
totalCount
) VALUES (
#{logServiceId},
#{timeFrom},
#{timeTo},
#{totalCount}
);

mapper.select:
SELECT * FROM log_histograms
WHERE logServiceId = #{logServiceId}
ORDER BY gmtCreate DESC
limit #{pageStart}, #{length}

SELECT * FROM log_service_path


        tagPath LIKE "%" #{tagPath} "%"


        AND serverGroupId = #{serverGroupId}


ORDER BY searchCnt DESC
limit #{start}, #{length}

SELECT * FROM log_service_path
WHERE tagPath = #{tagPath}
AND serverGroupId = #{serverGroupId}

SELECT count(*) from log_service_path


        tagPath LIKE "%" #{tagPath} "%"


        AND serverGroupId = #{serverGroupId}

mapper.insert:
INSERT INTO log_service_servergroup_cfg (
serverGroupId,
serverGroupName,
project,
logstore,
topic
) VALUES (
#{serverGroupId},
#{serverGroupName},
#{project},
#{logstore},
#{topic}
);

mapper.update:
UPDATE log_service_servergroup_cfg
SET project = #{project},
logstore = #{logstore},
topic = #{topic}
WHERE id = #{id}

mapper.insert:
INSERT INTO log_service_path (
serverGroupId,
tagPath,
searchCnt
) VALUES (
#{serverGroupId},
#{tagPath},
#{searchCnt}
);

mapper.update:
