mapper.insert:
INSERT ignore INTO cmdb_systems(
    systemName,
    systemUrl,
    imgUrl,
    systemDesc,
    owner
) VALUES (
    #{systemName},
    #{systemUrl},
    #{imgUrl},
    #{systemDesc},
    #{owner}
);

mapper.update:
UPDATE cmdb_systems
SET imgUrl = #{imgUrl},
systemUrl = #{systemUrl},
systemDesc = #{systemDesc},
owner = #{owner}
WHERE id = #{id}
